import React from 'react';
import {
  Card,
  CardContent,
  Container,
  Grid,
  Typography
} from '@material-ui/core';
import { gql, useQuery } from '@apollo/client';

/*
  GraphQL
*/
const GET_SUBMISSIONS = gql`
  query getAllSubmissions{
    submissions{
      _id
      score
      strength
      weakness
    }
  }
`;

/*
  Component
*/
function calculateNPS(data){
  var detractors =0;
  var passives = 0;
  var promoters = 0;
  var totalCount = 0;
  data.submissions.map((submission) => {
    if(submission.score <= 6){
      detractors++;
    } else if(submission.score <=8){
      passives++;
    } else {
      promoters++;
    };
    totalCount++;
  });
  var NPS = 100 * (promoters/totalCount - detractors/totalCount);
  console.log(detractors);
  console.log(promoters);
  console.log(NPS);
}

export default function Admin(){
  const {loading, data} = useQuery(GET_SUBMISSIONS);

  if (loading) return 'Loading...';

  calculateNPS(data);

  return(
    <div style={{'flexGrow': 1}}>
    <Grid container spacing={3}>
    <Grid item xs={12}>
      <Card>
        <Typography>NPS Score:</Typography>
      </Card>
    </Grid>
    {
      data.submissions.map(submission => (
        <Grid item xs={6} sm={4} key={submission._id}>
          <Card variant="outlined">
            <CardContent>
              <Typography>Score: {submission.score}</Typography>
              <Typography>Strength: {submission.strength}</Typography>
              <Typography>Weakness: {submission.weakness}</Typography>
            </CardContent>
          </Card>
        </Grid>
      ))
    }
    </Grid>
    </div>
  );
};
