import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import {
  BrowserRouter,
  Link,
  Redirect,
  Route,
  Switch
} from 'react-router-dom';
import { ApolloClient, InMemoryCache, ApolloProvider } from '@apollo/client';
import {
  AppBar,
  Button,
  IconButton,
  Toolbar
} from '@material-ui/core';
import { ChatBubble as HomeIcon } from '@material-ui/icons';
import { withStyles } from '@material-ui/styles';

import Form from './Components/Form';
import Admin from './Components/Admin';
import Login from './Components/Login';
import { authContext, useAuth } from './Components/utils';

const apolloClient = new ApolloClient({
  uri: 'http://localhost:4001/graphql',
  cache: new InMemoryCache()
});

/*
  Components
*/
const authenticate = {
  isAuthenticated: false,
  signin() {
    authenticate.isAuthenticated = true;
  },
  signout() {
    authenticate.isAuthenticated = false;
  }
};

function useProvideAuth() {
  const [user, setUser] = useState(null);

  const signin = () => {
    return authenticate.signin(() => {
      setUser("user");
    });
  };

  const signout = () => {
    return authenticate.signout(() => {
      setUser(null);
    });
  };

  return {
  user,
  signin,
  signout
  };
};


function ProvideAuth({ children }){
  const auth = useProvideAuth();
  return (
    <authContext.Provider value={auth}>
      {children}
    </authContext.Provider>
  );
};

function PrivateRoute({ children, ...rest }) {
  let auth = useAuth();
  console.log('authy', auth);
  return (
    <Route
      {...rest}
      render={({ location }) =>
        auth.user ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: "/login",
              state: { from: location }
            }}
          />
        )
      }
    />
  );
};

function App(){
  return(
    <ApolloProvider client={apolloClient}>
      <ProvideAuth>
      <BrowserRouter>
      <AppBar color="transparent" position="sticky" style={{boxShadow: 'none'}}>
      <Toolbar>
        <IconButton edge="start" component={Link} to="/">
            <HomeIcon />
        </IconButton>
        <Button component={Link} to="/admin">
          Admin
        </Button>
      </Toolbar>
      </AppBar>
      <Switch>
        <Route path="/login">
          <Login />
        </Route>
        <PrivateRoute path="/admin">
          <Admin />
        </PrivateRoute>
        <Route path="/">
          <Form />
        </Route>
      </Switch>
      </BrowserRouter>
</ProvideAuth>
      <a
        href="https://github.com/ckraczkowsky91"
        style={{
          bottom: 0,
          color: 'lightgrey',
          fontFamily: "'Alata', sans-serif",
          fontSize: '1.5rem',
          left: '50%',
          opacity: 0.5,
          position: 'fixed',
          textDecoration: 'none',
          transform: 'translate(-50%, 0)'
        }}>© Colin Kraczkowsky</a>
    </ApolloProvider>
  );
};

ReactDOM.render(
  <App />, document.getElementById('root')
);
