import React from 'react';
import {
  Button,
  Container,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Slider,
  Typography
} from '@material-ui/core';
import { withStyles } from '@material-ui/styles';
import { gql, useMutation } from '@apollo/client';

const HeaderText = withStyles({
  root: {
    'font-family': "'Alata', sans-serif",
    'font-size': '1.9rem'
  }
})(Typography);

const SubtitleText = withStyles({
  root: {
    'font-family': "'Alata', sans-serif",
    'font-size': '1.4rem',
    'color': 'lightgrey'
  }
})(Typography);

const StyledSlider = withStyles({
  root: {
    'margin-top': '40px'
  },
  markLabel: {
    'font-family': "'Alata', sans-serif",
    'font-size': '1.5rem'
  },
  valueLabel: {
    'font-family': "'Alata', sans-serif",
    'font-size': '1rem'
  }
})(Slider);

const StyledButton = withStyles({
  root: {
    'display': 'block',
    'margin': '40px auto 0px'
  }
})(Button);

const CREATE_SUBMISSION = gql`
  mutation createSubmission($score: Int!){
    createSubmission(score: $score){
      _id
      score
    }
  }
`;

export default function Form(){
  const [open, setOpen] = React.useState(false);
  const [score, setScore] = React.useState(10);
  const [submitterId, setSubmitterId] = React.useState(null);
  const [createSubmission, {data}] = useMutation(CREATE_SUBMISSION);

  const marks = [
    {
      value: 1,
      label: 1
    },
    {
      value: 2,
      label: 2
    },
    {
      value: 3,
      label: 3
    },
    {
      value: 4,
      label: 4
    },
    {
      value: 5,
      label: 5
    },
    {
      value: 6,
      label: 6
    },
    {
      value: 7,
      label: 7
    },
    {
      value: 8,
      label: 8
    },
    {
      value: 9,
      label: 9
    },
    {
      value: 10,
      label: 10
    }
  ];

  const handleOnChange = (event, value) => {
    setScore(value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    createSubmission({variables: {score: score}})
    .then((response) => {
      setSubmitterId(response.createSubmission._id);
    })
    .then(() => {
      setOpen(true);
    });
  };

  const closeDialog = () => {
    setOpen(false);
  };

  return(
    <Container style={{marginTop: '40px'}}>
      <HeaderText>On a scale of 0 to 10, how likely are you to recommend me as a person to work with?</HeaderText>
      <SubtitleText>Your submission will be completely anonymous as honest feedback is the goal!</SubtitleText>
      <form onSubmit={handleSubmit}>
        <StyledSlider
          defaultValue={10}
          marks={marks}
          max={10}
          min={1}
          onChange={handleOnChange}
          steps={10}
          valueLabelDisplay="on"/>
          <StyledButton type="submit" color="primary" variant="contained">Submit</StyledButton>
      </form>
      <Dialog open={open} onClose={closeDialog}>
        <DialogTitle>Thank you!</DialogTitle>
        <DialogContent>
          <DialogContentText>Your feedback has been submitted anonymously as </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button color="primary" onClick={closeDialog} variant="outlined">Close</Button>
        </DialogActions>
      </Dialog>
    </Container>
  )
};
