import React from 'react';
import {
  Button,
  Container,
  Slider,
  Typography
} from '@material-ui/core';
import { withStyles } from '@material-ui/styles';
import { ApolloClient, gql, InMemoryCache, ApolloProvider } from '@apollo/client';

const apolloClient = new ApolloClient({
  uri: 'http://localhost:4001/graphql',
  cache: new InMemoryCache()
})

const StyledSlider = withStyles({
  root: {
    'margin-top': '40px'
  },
  markLabel: {
    'font-family': "'Alata', sans-serif",
    'font-size': '1.5rem'
  },
  valueLabel: {
    'font-family': "'Alata', sans-serif",
    'font-size': '1rem'
  }
})(Slider);

const HeaderText = withStyles({
  root: {
    'font-family': "'Alata', sans-serif",
    'font-size': '2rem'
  }
})(Typography);

const SubtitleText = withStyles({
  root: {
    'font-family': "'Alata', sans-serif",
    'font-size': '1rem',
    'color': 'lightslategrey'
  }
})(Typography);

export default function App(){
  const [score, setScore] = React.useState(10);

  const marks = [
    {
      value: 1,
      label: 1
    },
    {
      value: 2,
      label: 2
    },
    {
      value: 3,
      label: 3
    },
    {
      value: 4,
      label: 4
    },
    {
      value: 5,
      label: 5
    },
    {
      value: 6,
      label: 6
    },
    {
      value: 7,
      label: 7
    },
    {
      value: 8,
      label: 8
    },
    {
      value: 9,
      label: 9
    },
    {
      value: 10,
      label: 10
    }
  ];

  const handleOnChange = (event, value) => {
    setScore(value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
  };

  return(
    <ApolloProvider client={apolloClient}>
      <Container>
        <HeaderText>On a scale of 0 to 10, how likely are you to recommend me as a person to work with?</HeaderText>
        <SubtitleText>Your submission will be completely anonymous as your honest feedback is the goal!</SubtitleText>
        <form onSubmit={handleSubmit}>
          <StyledSlider
            defaultValue={10}
            marks={marks}
            max={10}
            min={1}
            onChange={handleOnChange}
            steps={10}
            valueLabelDisplay="on"/>
          <Button type="submit" color="primary" variant="contained">Submit</Button>
        </form>
      </Container>
    </ApolloProvider>
  )
};
